mapper.insert:
INSERT INTO images (
    reference_id, reference_type, accommodation_id, room_id, image_url, is_main, caption, created_at
)
VALUES (
    #{referenceId}, #{referenceType}, 

        #{accommodationId}
        NULL
    ,

        #{roomId}
        #{referenceId}
        NULL
    ,
    #{imageUrl}, #{isMain}, #{caption}, NOW()
)

mapper.select:
SELECT i.* FROM images i
WHERE i.reference_id = #{referenceId} 
AND i.reference_type = #{referenceType} 
AND i.is_main = true
LIMIT 1

mapper.update:
UPDATE images
SET 
    image_url = #{imageUrl},
    caption = #{caption}
WHERE image_id = #{imageId}

UPDATE images
SET is_main = false
WHERE reference_id = #{referenceId} AND reference_type = #{referenceType};

UPDATE images
SET is_main = true
WHERE image_id = #{imageId}

mapper.insert:
